mapper.select:
SELECT *
FROM tbl_user 
WHERE
	user_id = #{user_id}
	AND user_pw = #{user_pw}
	AND user_admin='N'

SELECT *
FROM tbl_user 
WHERE
	user_id = #{user_id}
	AND user_pw = #{user_pw}
	AND user_admin='Y'

SELECT *
FROM tbl_user 
WHERE
	user_id = #{user_id}
	AND user_pw = #{user_pw}
	AND user_admin='M'
